import React, { useState, useRef, useCallback } from 'react';
import './AudioRecorder.css';

interface AudioRecorderProps {
  onRecordingComplete: (audioBlob: Blob) => void;
  onPauseRecording: (audioBlob: Blob) => void;
  onProcessingComplete: () => void;
  isProcessing: boolean;
  conversationUuid: string | null;
  onStartNewConversation: () => string;
  onEndConversation: () => void;
}

const AudioRecorder: React.FC<AudioRecorderProps> = ({ onRecordingComplete, onPauseRecording, onProcessingComplete, isProcessing, conversationUuid, onStartNewConversation, onEndConversation }) => {
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const chunksRef = useRef<Blob[]>([]);
  const streamRef = useRef<MediaStream | null>(null);
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  const initializeMediaRecorder = useCallback(async () => {
    if (!streamRef.current) {
      streamRef.current = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true,
        }
      });
    }

    const mediaRecorder = new MediaRecorder(streamRef.current, {
      mimeType: 'audio/webm;codecs=opus'
    });

    mediaRecorderRef.current = mediaRecorder;
    chunksRef.current = [];

    mediaRecorder.ondataavailable = (event) => {
      if (event.data.size > 0) {
        chunksRef.current.push(event.data);
      }
    };

    mediaRecorder.start();
    console.log('MediaRecorder started successfully');
  }, []);

  const startRecording = useCallback(async () => {
    try {
      console.log('Starting recording...');
      await initializeMediaRecorder();
      setIsRecording(true);
      setRecordingTime(0);

      timerRef.current = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);

      console.log('Recording started successfully');
    } catch (error) {
      console.error('Error starting recording:', error);
      alert('Unable to access microphone. Please check your permissions.');
    }
  }, [initializeMediaRecorder]);

  const stopRecording = useCallback(() => {
    if (mediaRecorderRef.current && (isRecording || isPaused)) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
      setIsPaused(false);

      // Clear timer
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }

      // Stop the media stream
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
        streamRef.current = null;
      }

      // Send the final recording if there are chunks
      setTimeout(() => {
        if (chunksRef.current.length > 0) {
          const audioBlob = new Blob(chunksRef.current, { type: 'audio/webm;codecs=opus' });
          onRecordingComplete(audioBlob);
        }
        chunksRef.current = [];
      }, 100);
    }
  }, [isRecording, isPaused, onRecordingComplete]);

  const pauseRecording = useCallback(async () => {
    if (mediaRecorderRef.current && isRecording) {
      // Stop the timer and set paused state
      setIsPaused(true);
      setIsRecording(false);
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }

      // Stop current recording and get the audio blob
      mediaRecorderRef.current.stop();

      // Wait for the recording to stop and get the complete audio blob
      setTimeout(() => {
        if (chunksRef.current.length > 0) {
          const audioBlob = new Blob(chunksRef.current, { type: 'audio/webm;codecs=opus' });
          console.log('Sending audio segment with size:', audioBlob.size);
          onPauseRecording(audioBlob);
        } else {
          console.error('No audio data available to send');
          resumeRecording();
        }
      }, 100);
    }
  }, [isRecording, onPauseRecording]);

  const resumeRecording = useCallback(async () => {
    if (isPaused && !isProcessing) {
      try {
        console.log('Resuming recording...');
        // Start a new MediaRecorder for the next segment
        await initializeMediaRecorder();
        setIsRecording(true);
        setIsPaused(false);

        // Resume the timer from where it left off
        timerRef.current = setInterval(() => {
          setRecordingTime(prev => prev + 1);
        }, 1000);

        console.log('Recording resumed successfully');
      } catch (error) {
        console.error('Error resuming recording:', error);
      }
    }
  }, [isPaused, isProcessing, initializeMediaRecorder]);

  // Auto-resume recording when processing completes
  React.useEffect(() => {
    if (isPaused && !isProcessing) {
      resumeRecording();
      onProcessingComplete();
    }
  }, [isPaused, isProcessing, resumeRecording, onProcessingComplete]);

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="audio-recorder">
      <div className="recording-status">
        {(isRecording || isPaused) && (
          <div className="recording-indicator">
            <div className={`pulse-dot ${isPaused ? 'paused' : ''}`}></div>
            <span>
              {isPaused && isProcessing ? 'Processing...' : 
               isPaused ? 'Paused' : 
               'Recording...'} {formatTime(recordingTime)}
            </span>
          </div>
        )}
      </div>

      <div className="recording-controls">
        {!isRecording && !isPaused ? (
          <button 
            className={`record-button start ${isProcessing ? 'processing' : ''}`}
            onClick={startRecording}
            disabled={isProcessing}
          >
            {isProcessing ? (
              <svg width="24" height="24" viewBox="0 0 24 24" fill="currentColor">
                <circle cx="12" cy="12" r="2">
                  <animate attributeName="r" values="2;4;2" dur="1s" repeatCount="indefinite"/>
                  <animate attributeName="opacity" values="1;0.5;1" dur="1s" repeatCount="indefinite"/>
                </circle>
              </svg>
            ) : (
              <svg width="24" height="24" viewBox="0 0 24 24" fill="currentColor">
                <circle cx="12" cy="12" r="8"/>
              </svg>
            )}
            {isProcessing ? 'Processing...' : 'Start Recording'}
          </button>
        ) : isPaused ? (
          <div className="recording-buttons">
            <button 
              className={`record-button resume ${isProcessing ? 'processing' : ''}`}
              onClick={resumeRecording}
              disabled={isProcessing}
            >
              <svg width="24" height="24" viewBox="0 0 24 24" fill="currentColor">
                <polygon points="8,5 19,12 8,19"/>
              </svg>
              {isProcessing ? 'Processing...' : 'Resume'}
            </button>
            <button 
              className="record-button stop"
              onClick={stopRecording}
              disabled={isProcessing}
            >
              <svg width="24" height="24" viewBox="0 0 24 24" fill="currentColor">
                <rect x="6" y="6" width="12" height="12" rx="2"/>
              </svg>
              Stop Recording
            </button>
          </div>
        ) : (
          <div className="recording-buttons">
            <button 
              className="record-button pause"
              onClick={pauseRecording}
              disabled={isProcessing}
            >
              <svg width="24" height="24" viewBox="0 0 24 24" fill="currentColor">
                <rect x="6" y="4" width="4" height="16"/>
                <rect x="14" y="4" width="4" height="16"/>
              </svg>
              Pause & Send
            </button>
            <button 
              className="record-button stop"
              onClick={stopRecording}
              disabled={isProcessing}
            >
              <svg width="24" height="24" viewBox="0 0 24 24" fill="currentColor">
                <rect x="6" y="6" width="12" height="12" rx="2"/>
              </svg>
              Stop Recording
            </button>
          </div>
        )}
      </div>

      <p className="recording-hint">
        {!isRecording && !isProcessing && "Press the button and share what's on your mind. Use 'Pause & Send' to send audio to AI."}
        {isRecording && !isProcessing && "Recording... Click 'Pause & Send' to get AI response or 'Stop' to end recording."}
        {isProcessing && "Processing your message with care..."}
      </p>
    </div>
  );
};

export default AudioRecorder;
