import React, { useState } from 'react';
import './App.css';
import AudioRecorder from './components/AudioRecorder';
import AudioPlayer from './components/AudioPlayer';
import TranscriptionHistory, { Conversation, ChatMessage } from './components/TranscriptionHistory';
import { AudioService, AudioResponse } from './services/audioService';

// Generate UUID function
const generateUUID = (): string => {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
};

function App() {
  const [isProcessing, setIsProcessing] = useState(false);
  const [currentResponse, setCurrentResponse] = useState<AudioResponse | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [conversations, setConversations] = useState<Conversation[]>([]);
  const [currentConversationUuid, setCurrentConversationUuid] = useState<string | null>(null);

  const handleRecordingComplete = async (audioBlob: Blob) => {
    setIsProcessing(true);
    setError(null);
    setCurrentResponse(null);

    try {
      const response = await AudioService.sendAudioToServer(audioBlob);

      if (response.success) {
        setCurrentResponse(response);
      } else {
        setError(response.error || 'Failed to process audio');
      }
    } catch (err: any) {
      setError(err.message || 'An unexpected error occurred');
    } finally {
      setIsProcessing(false);
    }
  };

  const handlePauseRecording = async (audioBlob: Blob) => {
    setIsProcessing(true);
    setError(null);
    setCurrentResponse(null);

    try {
      const response = await AudioService.sendAudioToServer(audioBlob);

      if (response.success && response.transcription) {
        setCurrentResponse(response);

        // Add transcription to history (latest at top)
        const newTranscription: Transcription = {
          id: Date.now().toString(),
          timestamp: new Date(),
          filename: response.filename || 'recording.webm',
          transcription: response.transcription,
          needHumanIntervention: response.needHumanIntervention || false,
        };

        setTranscriptions(prev => [newTranscription, ...prev]);
      } else {
        setError(response.error || 'Failed to process audio');
      }
    } catch (err: any) {
      setError(err.message || 'An unexpected error occurred');
    } finally {
      setIsProcessing(false);
    }
  };

  const handlePlaybackComplete = () => {
    // Optional: Add any cleanup or next steps after audio playback
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Empathy AI</h1>
        <p>A safe space to share your thoughts and feelings</p>
      </header>

      <main className="app-main">
        <div className="support-message">
          <h2>You're Not Alone</h2>
          <p>
            This is a judgment-free space where you can express yourself freely. 
            Our AI companion is here to listen with empathy and provide supportive guidance.
          </p>
        </div>

        <div className="main-content">
          <div className="recording-section">
            <AudioRecorder 
              onRecordingComplete={handleRecordingComplete}
              onPauseRecording={handlePauseRecording}
              onProcessingComplete={() => {}} // Empty callback as auto-resume is handled internally
              isProcessing={isProcessing}
            />

            {error && (
              <div className="error-message">
                <p>⚠️ {error}</p>
                <small>Please try again or check your connection.</small>
              </div>
            )}

            {currentResponse && currentResponse.success && (
              <AudioPlayer
                audioUrl={currentResponse.audioUrl || null}
                message={currentResponse.message}
                onPlaybackComplete={handlePlaybackComplete}
              />
            )}
          </div>

          <TranscriptionHistory transcriptions={transcriptions} />
        </div>

      </main>
    </div>
  );
}

export default App;
