import React, { useState } from 'react';
import './App.css';
import AudioRecorder from './components/AudioRecorder';
import AudioPlayer from './components/AudioPlayer';
import TranscriptionHistory, { Conversation, ChatMessage } from './components/TranscriptionHistory';
import { AudioService, AudioResponse } from './services/audioService';

// Generate UUID function
const generateUUID = (): string => {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
};

function App() {
  const [isProcessing, setIsProcessing] = useState(false);
  const [currentResponse, setCurrentResponse] = useState<AudioResponse | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [conversations, setConversations] = useState<Conversation[]>([]);
  const [currentConversationUuid, setCurrentConversationUuid] = useState<string | null>(null);

  const handleRecordingComplete = async (audioBlob: Blob) => {
    if (!currentConversationUuid) return;

    setIsProcessing(true);
    setError(null);
    setCurrentResponse(null);

    try {
      const response = await AudioService.sendAudioToServer(audioBlob, currentConversationUuid);

      if (response.success) {
        setCurrentResponse(response);
      } else {
        setError(response.error || 'Failed to process audio');
      }
    } catch (err: any) {
      setError(err.message || 'An unexpected error occurred');
    } finally {
      setIsProcessing(false);
    }
  };

  const handlePauseRecording = async (audioBlob: Blob) => {
    if (!currentConversationUuid) {
      setError('No active conversation');
      return;
    }

    // First, add the user message immediately (before API call)
    const timestamp = new Date();
    const tempUserMessage: ChatMessage = {
      id: Date.now().toString(),
      timestamp,
      type: 'user',
      content: 'Processing your message...',
      filename: 'recording.webm',
    };

    // Add user message to show it's being processed
    setConversations(prev => {
      const updatedConversations = [...prev];
      if (updatedConversations.length > 0) {
        updatedConversations[0].messages.push(tempUserMessage);
      }
      return updatedConversations;
    });

    setIsProcessing(true);
    setError(null);
    setCurrentResponse(null);

    try {
      const response = await AudioService.sendAudioToServer(audioBlob, currentConversationUuid);

      if (response.success && response.transcription && response.transcriptionReply) {
        setCurrentResponse(response);

        // Update the user message with actual transcription and add AI reply
        const actualUserMessage: ChatMessage = {
          id: tempUserMessage.id,
          timestamp,
          type: 'user',
          content: response.transcription,
          filename: response.filename,
        };

        const aiMessage: ChatMessage = {
          id: (Date.now() + 1).toString(),
          timestamp: new Date(timestamp.getTime() + 100),
          type: 'ai',
          content: response.transcriptionReply,
          needHumanIntervention: response.needHumanIntervention || false,
        };

        setConversations(prev => {
          const updatedConversations = [...prev];
          if (updatedConversations.length > 0) {
            // Replace the temp user message and add AI response
            const messages = updatedConversations[0].messages;
            const lastMessageIndex = messages.length - 1;
            messages[lastMessageIndex] = actualUserMessage;
            messages.push(aiMessage);
          }
          return updatedConversations;
        });
      } else {
        // Remove the temp user message if there was an error
        setConversations(prev => {
          const updatedConversations = [...prev];
          if (updatedConversations.length > 0) {
            updatedConversations[0].messages.pop(); // Remove temp message
          }
          return updatedConversations;
        });
        setError(response.error || 'Failed to process audio');
      }
    } catch (err: any) {
      // Remove the temp user message if there was an error
      setConversations(prev => {
        const updatedConversations = [...prev];
        if (updatedConversations.length > 0) {
          updatedConversations[0].messages.pop(); // Remove temp message
        }
        return updatedConversations;
      });
      setError(err.message || 'An unexpected error occurred');
    } finally {
      setIsProcessing(false);
    }
  };

  const handleStartNewConversation = (): string => {
    const uuid = generateUUID();
    setCurrentConversationUuid(uuid);

    // Create new conversation
    const newConversation: Conversation = {
      id: uuid,
      messages: [],
      startTime: new Date(),
    };

    setConversations(prev => [newConversation, ...prev]);
    console.log('Started new conversation with UUID:', uuid);
    return uuid;
  };

  const handleEndConversation = () => {
    setCurrentConversationUuid(null);
    console.log('Ended conversation');
  };

  const handlePlaybackComplete = () => {
    // Optional: Add any cleanup or next steps after audio playback
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Empathy AI</h1>
        <p>A safe space to share your thoughts and feelings</p>
      </header>

      <main className="app-main">
        <div className="support-message">
          <h2>You're Not Alone</h2>
          <p>
            This is a judgment-free space where you can express yourself freely. 
            Our AI companion is here to listen with empathy and provide supportive guidance.
          </p>
        </div>

        <div className="main-content">
          <div className="recording-section">
            <AudioRecorder 
              onRecordingComplete={handleRecordingComplete}
              onPauseRecording={handlePauseRecording}
              onProcessingComplete={() => {}} // Empty callback as auto-resume is handled internally
              isProcessing={isProcessing}
              conversationUuid={currentConversationUuid}
              onStartNewConversation={handleStartNewConversation}
              onEndConversation={handleEndConversation}
            />

            {error && (
              <div className="error-message">
                <p>⚠️ {error}</p>
                <small>Please try again or check your connection.</small>
              </div>
            )}

            {currentResponse && currentResponse.success && (
              <AudioPlayer
                audioUrl={currentResponse.audioUrl || null}
                message={currentResponse.message}
                onPlaybackComplete={handlePlaybackComplete}
              />
            )}
          </div>

          <TranscriptionHistory conversations={conversations} />
        </div>

      </main>
    </div>
  );
}

export default App;
