import React from 'react';
import './TranscriptionHistory.css';

export interface Transcription {
  id: string;
  timestamp: Date;
  filename: string;
  transcription: string;
  needHumanIntervention: boolean;
}

interface TranscriptionHistoryProps {
  transcriptions: Transcription[];
}

const TranscriptionHistory: React.FC<TranscriptionHistoryProps> = ({ transcriptions }) => {
  const formatTime = (date: Date) => {
    return date.toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit',
      second: '2-digit'
    });
  };

  return (
    <div className="transcription-history">
      <div className="history-header">
        <h3>Transcription History</h3>
        <div className="transcription-count">
          {transcriptions.length} {transcriptions.length === 1 ? 'transcription' : 'transcriptions'}
        </div>
      </div>

      <div className="transcriptions-list">
        {transcriptions.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">
              <svg width="48" height="48" viewBox="0 0 24 24" fill="currentColor">
                <path d="M12 14C13.66 14 15 12.66 15 11V5C15 3.34 13.66 2 12 2S9 3.34 9 5V11C9 12.66 10.34 14 12 14ZM13.5 11C13.5 11.83 12.83 12.5 12 12.5S10.5 11.83 10.5 11V5C10.5 4.17 11.17 3.5 12 3.5S13.5 4.17 13.5 5V11ZM19 11C19 15.14 15.86 18.3 12 18.3S5 15.14 5 11H6.5C6.5 14.31 9.19 17 12.5 17V19.5H11V21H13V19.5H12.5C16.81 19.5 20.5 15.81 20.5 11.5H19V11Z"/>
              </svg>
            </div>
            <p>No transcriptions yet</p>
            <small>Start recording and click "Pause & Send" to see transcriptions here</small>
          </div>
        ) : (
          transcriptions.map((transcription) => (
            <div key={transcription.id} className="transcription-card">
              <div className="card-header">
                <div className="timestamp">
                  {formatTime(transcription.timestamp)}
                </div>
                {transcription.needHumanIntervention && (
                  <div className="intervention-badge">
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                      <path d="M1 21H23L12 2L1 21ZM13 18H11V16H13V18ZM13 14H11V10H13V14Z"/>
                    </svg>
                    Attention Needed
                  </div>
                )}
              </div>

              <div className="transcription-content">
                <p>{transcription.transcription}</p>
              </div>

              <div className="card-footer">
                <small className="filename">
                  <svg width="12" height="12" viewBox="0 0 24 24" fill="currentColor">
                    <path d="M14 2H6C4.9 2 4 2.9 4 4V20C4 21.1 4.89 22 5.99 22H18C19.1 22 20 21.1 20 20V8L14 2ZM18 20H6V4H13V9H18V20Z"/>
                  </svg>
                  {transcription.filename}
                </small>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default TranscriptionHistory;
